sp_widget.client_script:
function($scope,$timeout) {
  /* widget controller */
  var c = this;
	$timeout(function(){
		switch (c.data.id) {
			case 'cisco_userProfile':$scope.update_breadcrumbs("User Profile");break;
			case 'cisco_alarms':$scope.update_breadcrumbs("Alarms");break;
			case 'cisco_incidents':$scope.update_breadcrumbs("Incidents");break;
			case 'cisco_servers':$scope.update_breadcrumbs("Servers");break;
			case 'cisco_hx':$scope.update_breadcrumbs("HyperFlex Clusters");break;
			case 'cisco_fi':$scope.update_breadcrumbs("Fabric Interconnects");break;
			case 'cisco_support':$scope.update_breadcrumbs("Support");break;
			default: $scope.update_breadcrumbs("Dashboard");
		}
	})

$scope.update_breadcrumbs=function(page_name){
	$rootScope.$broadcast('sp.update.breadcrumbs',[{"label":page_name,"url":"#"}]);
}	
}

