sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.id=$sp.getParameter('id');
	//gs.addErrorMessage(data.id)
	data.list_items_json={
		items:[
			{
				"icon":"dashboard.svg",
				"active_icon":"dashboard-active.svg",
				"name":"Dashboard",
				"url":"?id=cisco_homepage",
				"active":(data.id==undefined||data.id=='cisco_homepage')?true:false
			},
			{
				"icon":"alarms.svg",
				"active_icon":"alarms-active.svg",
				"name":"Alarms",
				"url":"?id=cisco_alarms",
				"active":(data.id=='cisco_alarms')?true:false
			},
			{
				"icon":"incidents.svg",
				"active_icon":"incidents-active.svg",
				"name":"Incidents",
				"url":"?id=cisco_incidents",
				"active":(data.id=='cisco_incidents'||data.id=="cisco_incidents_form")?true:false
			},
			{
				"icon":"servers.svg",
				"active_icon":"servers-active.svg",
				"name":"Servers",
				"url":"?id=cisco_servers",
				"active":(data.id=='cisco_servers'||data.id=='cisco_servers_form')?true:false
			},
			{
				"icon":"hx.svg",
				"active_icon":"hx-active.svg",
				"name":"HyperFlex Clusters",
				"url":"?id=cisco_hx",
				"active":(data.id=='cisco_hx'||data.id=='cisco_hx_form')?true:false
			},
			{
				"icon":"fi.svg",
				"active_icon":"fi-active.svg",
				"name":"Fabric Interconnects",
				"url":"?id=cisco_fi",
				"active":(data.id=='cisco_fi'||data.id=='cisco_fi_form')?true:false
			},
			{
				"icon":"support.svg",
				"active_icon":"support-active.svg",
				"name":"Support",
				"url":"?id=cisco_support",
				"active":(data.id=='cisco_support')?true:false
			}
		]};
})();
