sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	this.logger = new Cisco_SNLogger();

	try{
		var topic = current.topic+'';
		current.state = "processing";
		current.update();

		var call="";
		var callFunction ="";

		switch(topic){

			case "Start Import":                        call = new Cisco_ImportInventory();
														callFunction = call.importDeviceRegistrationData( current);

														call = new Cisco_DAOScript();

														call.insertIntoQueue('Start Inventory Import', '', 'Server', current.intersight+'');
														call.insertIntoQueue('Start Inventory Import', '', 'Fabric Interconnects', current.intersight+'');
														call.insertIntoQueue('Start Inventory Import', '', 'HX clusters', current.intersight+'');
													  //  call.insertIntoQueue('Start Alarms Import', '', 'Alarms', current.intersight+'');

														current.state = "completed";
														break;	

			case "Start Inventory Import":              call = new Cisco_ImportInventory();
														callFunction = call.startInventoryImport( current);
														current.state = "completed";
														break;	

			case "Process Inventory Endpoint":          call = new Cisco_ImportInventory();
														callFunction = call.processInventoryEndpoint( current);
														current.state = "completed";
														break;

			case "Process Inventory Response":          call = new Cisco_ImportInventory();
														callFunction = call.processInventoryResponse( current);
														current.state = "completed";
														break;	

			case "Start Alarms Import":                 call = new Cisco_ImportInventory();
														callFunction = call.startInventoryImport( current);
														current.state = "completed";
														break;
		}

		this.logger.info("Cisco Import Queues Post Insert BR ran sucessfully");

	}catch(e) {

		current.state = "error";
		this.logger.error("Exception caught inside Cisco Import Queues Post Insert BR : "+e);
	}finally{

		current.update();
	}

})(current, previous);
