sys_script_include.script:
var Cisco_SNLogger = Class.create();
Cisco_SNLogger.prototype = {
	initialize: function() {

		this.logLevel = gs.getProperty('x_caci_cisco_inter.intersight_log_verbosity');
		this.plugin_version = gs.getProperty('x_caci_cisco_inter.plugin.version');
	},

	debug: function(s) {

		if (this.logLevel >= 3)
			gs.info( this.plugin_version+' DEBUG >> '+s );
	},

	info:  function(s) {

		if (this.logLevel >= 2)
			gs.info(this.plugin_version+' '+ s );
	},

	warn: function(s) {

		if (this.logLevel >= 1)
			gs.warn(this.plugin_version+' '+ s );
	},

	error: function(s) {

		if (this.logLevel >= 0)
			gs.error(this.plugin_version+' '+ s );
	},

	type: 'Cisco_SNLogger'
};
