sys_script_include.script:
x_caci_cisco_inter.cryptoJsLibrary;
var Cisco_SignatureCompute = Class.create();
Cisco_SignatureCompute.prototype = {
    initialize: function() {

		this.appUtil = new Cisco_AppUtil();
		this.logger = new Cisco_SNLogger();
    },

	computeDigest: function( body){

		var computedDigest = 'SHA-256=' + CryptoJS.enc.Base64.stringify(CryptoJS.SHA256(body));
		return computedDigest;
	},

	hash: function( targetUrl, host, computedDigest, method, currDate, secretKey){

		try{
			this.logger.debug("Inside Cisco_SignatureCompute.hash calculating hash.");

			method = method.toLowerCase();

			var kjursig = new KJUR.crypto.Signature({"alg": "SHA256withRSA"});
			kjursig.init( secretKey);

			var middleRef = '(request-target): ' + method + ' ' +  targetUrl.toLowerCase() + '\n' + 
				'date: ' + currDate + '\n' + 
				'digest: ' + computedDigest + '\n' + 
				'host: ' + host ;

			kjursig.updateString(middleRef);
			return kjursig.sign();
		}catch(e){
			this.logger.error("Exception caught inside Cisco_SignatureCompute.hash while calculating hash. Error :- "+e);
		}

	},

	signature: function( hash ){

		return  hextob64(hash) ;
	},


    type: 'Cisco_SignatureCompute'
};
