sys_transform_map.script:
(function transformRow(source, target, map, log, isUpdate) {

	var appUtil = new Cisco_AppUtil();
	var snLogger  = new Cisco_SNLogger();

	try{

		if(source.tags){

			var tags = appUtil.parse( source.tags);
			for(var i in tags){
				var tag = tags[i];

				target.tags[tag.Key+''] = tag.Value+'';
			}	
		}

		if(source.cpucapacity){
			var CPUCapacity = source.cpucapacity;
			CPUCapacity = Math.round(CPUCapacity * 100) / 100;
			target.cpu_capacity = CPUCapacity;	
		}

		//target.install_status=1;
		target.operational_status=1;
	}catch(e){

		snLogger.error('Error in Cisco Server TM '+ e);
	}

})(source, target, map, log, action==="update");
