sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	var appUtil = new Cisco_AppUtil();
	var daoScript = new Cisco_DAOScript(); 

	var registerDeviceMoid = appUtil.parse(source.registereddevice).Moid+'';
	var regDevice = daoScript.getRegisteredDevice(registerDeviceMoid);
	var configuration_item = daoScript.getCmdbRecRef(source, regDevice.platform_type);
	if(configuration_item){
		target.ci_record = configuration_item;
		target.source_name = regDevice.sys_id;
	}	
	if((regDevice.execution_mode && regDevice.execution_mode+'' != "Normal") || source.severity+''=="Cleared" || !configuration_item )
		ignore = true;

})(source, map, log, target);
