sys_ui_action.script:
var logger = new Cisco_SNLogger();

try{
	current.api_key_id = current.api_key_id.trim();
	current.secret_key = current.secret_key.trim();
	current.host_name = current.host_name.trim();
	current.update();
	var mid_flag = 1;
	if(current.on_premises  && current.mid_server.status == 'Down' && current.mid_server.validated == 'false'  ){
		gs.addErrorMessage('Mid Server is Down and Not Validated , Please check your Connection');
		mid_flag = 0;
	}	
	else if(current.on_premises  && current.mid_server.status == 'Down' && current.mid_server.validated == 'true'){
		gs.addErrorMessage('Mid Server is Down , Please check your Connection');
		mid_flag = 0;
	}
	else if(current.on_premises  && current.mid_server.status == 'Up' && current.mid_server.validated == 'false'){
		gs.addErrorMessage('Mid Server is Up But its Not Validated, Please check your Connection');
		mid_flag = 0;
	}
	if(mid_flag == 1){
	var appUtil = new Cisco_AppUtil();
	var jsonResp = appUtil.makeApiCall( 'get', '/compute/PhysicalSummaries?$top=1', '', current);

	var httpstatus = jsonResp.getStatusCode();
	if(httpstatus==200){
		gs.addInfoMessage('Valid Credentials');
		current.validated='YES';
		current.update();

		var daoScrpt = new Cisco_DAOScript();
		daoScrpt.insertIntoQueue('Start Import', '', '', current.sys_id+'');

		gs.setProperty('x_caci_cisco_inter.start.import', true);

		action.setRedirectURL('x_caci_cisco_inter_inventory_queue_list.do?sysparm_query=state!%3Dcompleted');
	}
	else{
		if(httpstatus==401)
			gs.addErrorMessage('Invalid API Key ID/Secret Key.');
		else
			gs.addErrorMessage('Invalid HostName. Try removing http:// or https:// from hostname if present and try again.');

		gs.setProperty('x_caci_cisco_inter.start.import', false);

		current.validated='NO';
		current.update();
		action.setRedirectURL(current);
	}
	}else{
			action.setRedirectURL(current);
		}



}catch(e){
	logger.error("Exception caught inside Save & Import UI Action while starting import. Error :- "+e);
}
