sysauto_script.condition:
var daoScrpt = new Cisco_DAOScript();
var runScheduleJob = daoScrpt.checkWhetherScheduleJobIsRunning();
gs.getProperty('x_caci_cisco_inter.start.import')+'' == 'true'  && runScheduleJob

sysauto_script.script:
var snLogger = new Cisco_SNLogger();
var appUtil = new Cisco_AppUtil();

try {
	var daoScrpt = new Cisco_DAOScript();
    snLogger.debug('Inside Cisco_ImportAlarms Schedule Job for running it periodically.');
    daoScrpt.insertIntoQueue('Start Alarms Import', '', 'Alarms', appUtil.configuration.property.authSysId);
} catch (e) {
    snLogger.error('Exception caught Inside Cisco_ImportAlarms Schedule Job for running it periodically on every 3 hours');
}
